mapper.update:
UPDATE eatstamp.member
SET mem_admin_auth = #{mem_admin_auth}
WHERE mem_num = #{mem_num}

mapper.select:
SELECT report_num, mem_num, mem_num2, report_why, report_ynCode, s_num, cmt_num, report_return FROM( SELECT @rownum:=@rownum+1 AS rn, report_num, mem_num, mem_num2, report_why, report_ynCode, s_num, cmt_num, report_return FROM eatstamp.report , (SELECT @rownum:=0) TMP
AND mem_num LIKE CONCAT('%', #{searchKeyword}, '%')
AND mem_num2 LIKE CONCAT('%', #{searchKeyword}, '%')
) t1 WHERE rn BETWEEN #{start} and #{end}
