mapper.update:
UPDATE eatstamp.member
SET mem_admin_auth = #{mem_admin_auth}
WHERE mem_num = #{mem_num}

mapper.select:
SELECT report_num, mem_num, mem_num2, report_why, report_ynCode, s_num, cmt_num, report_return, report_link FROM ( SELECT @rownum:=@rownum+1 AS rn, report_num, mem_num, mem_num2, report_why, report_ynCode, s_num, cmt_num, report_return, report_link FROM report r, (SELECT @rownum:=0) TMP
WHERE EXISTS ( SELECT 1 FROM member m WHERE m.mem_email LIKE CONCAT('%', #{search_keyword}, '%') AND r.mem_num = m.mem_num
WHERE EXISTS ( SELECT 1 FROM member m WHERE m.mem_email LIKE CONCAT('%', #{search_keyword}, '%') AND r.mem_num2 = m.mem_num
)
) t1 WHERE rn BETWEEN #{start} and #{end}
